import { navigate } from '@reach/router';
import { Field, Form, Formik } from 'formik';
import React from 'react';
import ShowTodos from './ShowTodos';

// Credit: AddTodo is adapted from
// https://dev.to/able/building-and-consuming-a-json-api-with-rails-and-react-42p6
const AddTodo = () => {
    const handleSubmit = (values) => {
        const requestTodos = async () => {
            // Get the CSRF token generated by Rails to send it
            // as a header in the request to create a new to-do,
            // so that Rails will recognize the request as valid.
            const csrfToken = document.querySelector('meta[name=csrf-token]').content;
            const response = await fetch('/api/todos', {
                method: 'POST',
                credentials: 'include',
                headers: {
                    'Content-Type': 'application/vnd.api+json',
                    'X-CSRF-Token': csrfToken
                },
                body: JSON.stringify({ data: values })
            });
            switch (response.status) {
                case 200: // OK
                // fallthrough
                case 204: // No Content
                // fallthrough
                case 201: // Created
                    navigate('/');
                    break;
                default:
                    alert('The To-Do could not be created. \n\n'.concat(
                        `Title: ${values.attributes.title} \nBody: ${values.attributes.body}\n`
                    ))
                    break;
            }
        };
        requestTodos();
    };

    return (
        <div>
            <h2>Add a new To Do item</h2>
            <Formik
                initialValues={{
                    type: 'todos',
                    attributes: {
                        title: '',
                        body: ''
                    }
                }}
                onSubmit={handleSubmit}
            >
                {() => (
                    <Form>
                        <Field type="text" name="attributes.title" placeholder="Title" />
                        <Field type="text" name="attributes.body" placeholder="Details" />

                        <button type="submit">Add</button>
                    </Form>
                )}
            </Formik>

            <h2>Your current to-dos</h2>
            <ShowTodos />
        </div>
    );
};

export default AddTodo;
